+          rel.getInputs().forEach(input -> nodeInputs.get(nodeID)
+              .add(String.valueOf(input.getId())));
+        }
+      };
+
+      set.rels.forEach(addNode);
+      set.subsets.forEach(addNode);
+      set.rels.forEach(addLink);
+      set.subsets.forEach(addLink);
+    });
