+        int bitLength = value.bitLength();
+        byte[] bytes = new byte[(bitLength + 7) / 8];
+        for (int i = 0 ; i < bitLength ; i += 8)
+            bytes[i/8] = value.shiftRight(bitLength - (i+1)*8).byteValue();
+        return new ByteOrderedPartitioner.BytesToken(bytes);
+    }
+}

