+        String value = System.getProperty("zookeeper.maxResponseCacheSize");
+        return value == null ? DEFAULT_RESPONSE_CACHE_SIZE : Integer.parseInt(value);
+    }
+
+    public static boolean isEnabled() {
+        return getResponseCacheSize() != 0;
+    }
