+        @Override
+        protected void endOfIteration() {
+            if (requestSeen != null) {
+                requestSeen.countDown();
+            }
+        }
+
+        @Override
+        protected void waitForEmptyPool() throws InterruptedException {
+            if (commitSeen != null) {
+                commitSeen.countDown();
+            }
+            super.waitForEmptyPool();
+            if (poolEmpytied != null) {
+                poolEmpytied.countDown();
+            }
+        }
