+public class PrepRequestProcessorMetricsTest extends ZKTestCase {
+    private static final Logger LOG = LoggerFactory.getLogger(PrepRequestProcessorMetricsTest.class);
+
+    ZooKeeperServer zks;
+    RequestProcessor nextProcessor;
